import React, { Component } from 'react';
import '../App.css';
import './combined.css';
import {Button, ButtonGroup} from 'react-bootstrap';
import {Modal, ModalHeader, ModalBody} from 'reactstrap';
import {Link} from 'react-router-dom'
import './modules/sample_data'
import { returnData } from './modules/sample_data';

var Datastore = require('nedb'), db = new Datastore({ filename: '/home/sandesh/se/database.db' });
db.loadDatabase(function(error){
    if(error)
        {console.log("Error while loading database: " , error);    
    }
})

class TableGen extends Component {
    constructor(props, context){
        super(props, context);
        this.state = {};
        if(this.props.type === "combined"){
            this.title = "All Publications:";
        }
        if(this.props.type === "books"){
            this.title = "Books:";
        }
        if(this.props.type === "research-papers"){
            this.title = "Research Papers:";
        }

        this.state = {
          modal: false,
          deleteMode: false,
          count: 1
        };

        this.adderLink = "addnew/" + this.props.type;
        this.editorLink = "editlist/";
        this.toggle = this.toggle.bind(this);
        this.setState({data: ''});
        this.setState({loaded: false});

        const myState = this.state;
        db.find({}, function(error, docs){
            myState.setState({data: docs});
            console.log(myState);
            console.log(docs);
            if(!error){
                myState.setState({loaded: true});
            }
        })
    }


    deleteItem(event){
        fetch("http://localhost:3001/deleteItem",{
            method: "POST"
        })
    }

    fetchData(){
        // db.find({}, function(error, docs){
        //     if(error){
        //         alert("There was an error fetching the data :'( ");
        //     }
        //     // console.log("Data fetched: ", docs);
        //     // reference.data = docs;
        // })
        console.log("Spider man.. spider man.. Does whatever a spider")
    }

    toggle() {
        this.setState({
            modal: !this.state.modal
        });
    }

    render(){
        return(
            <div className = "table container-fluid ">
                <Modal className = "small text-center" isOpen={this.state.modal} toggle={this.toggle} >
                    <ModalHeader toggle = {this.toggle}> Generate </ModalHeader>

                    <ModalBody>
                        Select format for <u>{this.title}</u> table to generate and download
                        or print.
                        <br /><br />
                        Table Format:
                        <form>
                            <select>
                                <option> Modern Language Association</option>
                                <option> Modern Language Association</option>
                                <option> Modern Language Association</option>
                                <option> Modern Language Association</option>
                            </select>
                        </form>

                        <br />
                        File Format:
                        <form>
                            <select>
                                <option>PDF</option>
                                <option>DOCX</option>
                            </select>
                        </form>
                        <br />
                        <ButtonGroup>
                            <Button bsStyle = "primary" >Generate and Download</Button>
                            <Button bsStyle = "primary" >Generate and Print</Button>
                        </ButtonGroup>
                        <br />&nbsp;
                    </ModalBody>
                </Modal>
                <div className = "container">
                    <h6>
                        {this.title}
                        <br />
                    </h6>
                    <br />
                    <table className = "small">
                        <tbody>
                            <tr>
                                <td> S.N.</td>
                                <td> Title </td>
                                <td> Name of Journal </td>
                                <td> Volume </td>
                                <td> Authors </td>
                                <td> Date </td>
                                <td> DOI </td>
                                <td> ISBN </td>
                                <td> Type </td>
                                <td> Edit </td>
                                <td> Delete </td>
                            </tr>
                            {   this.state.loaded && 
                                this.state.data.map((item, index)=>{
                                    return(<tr key={item.title+this.state.count}>
                                        <td>{this.state.count++}</td>
                                        <td>{item.title}</td>
                                        <td>{item.name_of_journal}</td>
                                        <td>{item.volume}</td>
                                        <td>// Remaining to fill</td>
                                        <td>{item.date}</td>
                                        <td>{item.doi}</td>
                                        <td>{item.isbn}</td>
                                        <td>{this.props.type}</td>
                                        <td>
                                            <Link to={this.editorLink + "" + item.title}>Edit</Link>
                                        </td>
                                        <td>
                                            <Button name={item.title} bsSize = "small" bsStyle="danger" onClick={this.deleteItem}>Delete</Button>
                                        </td>
                                    </tr>);
                                })
                            }
                        </tbody>
                    </table>
                    <br />
                    <ButtonGroup>
                        <Button bsSize = 'small' bsStyle = 'warning' onClick = {this.toggle}>Generate</Button>
                        {
                            this.props.type !== "combined"?
                            <Button bsSize = 'small' bsStyle = 'success'><Link to={this.adderLink} state={`type: this.props.type`} className="white">Add new Entry</Link></Button>
                            : ""
                        }
                    </ButtonGroup>
                </div>
            </div>
        );
    }
}

export default TableGen;
