import React from 'react';

import TopBar from '../topbar_component/topbar';

export default class Edit extends React.Component{
    constructor(props){
        super(props);
        this.fetchArticleFields = this.fetchArticleFields.bind(this);
        this.state = {
            articleID: '',
            articleType: '',         
            articleFields: [],
        }
        this.state.articleID = this.props.location.id;
        this.state.articleType = this.props.location.type;
        console.log(this.props.location.id);
    }

    fetchArticleFields(){

    }

    render(){
        var context = this;
        return(
            <div>
                <TopBar></TopBar>

            <div> 
                <div>
                    <Typography>
                        Enter the information related to {this.props.type}.
                    </Typography>
                    <br />
                    <Typography>
                        ( <span style = {{color: 'red'}}>*</span> denotes a required field )
                    </Typography>
                    <Table>
                        <TableBody>
                            { this.state.fieldList.map(function(item, index){
                                return (
                                    <TableRow key = {index}>
                                        <TableCell>
                                            {item.description}
                                            {item.required && <span style = {{color: 'red'}}>*</span> }
                                        </TableCell>
                                        <TableCell>
                                            <TextField 
                                            required = {item.required}
                                            name = "somename"
                                            id = "someid"
                                            className = "someClassName"
                                            key = {index}
                                            onChange = {context.inputChange(item.id)} placeholder = "Begin typing..." />
                                        </TableCell>
                                    </TableRow>
                                );   
                            })}
                            <TableRow>
                                <TableCell></TableCell>
                                <TableCell>
                                    <Button size = "large" color = "primary" onClick = {this.handleSubmit}>
                                        Submit.
                                    </Button>
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </div>
            </div>
            </div>
        )
    }
}
