import React from 'react';
import Typography from '@material-ui/core/Typography';

import {fieldList} from '../../database/fieldList';
import {Table,  TableBody, TableRow, TableCell, TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import addPublication from '../../database/insert';

import TopBar from '../topbar_component/topbar';

export default class Edit extends React.Component{
    constructor(props){
        super(props);
        this.handleProp = this.handleProp.bind(this);
        this.state = {
            articleID: '',
            articleType: '',  
            fieldList: []
        }
        this.state.articleID = this.props.location.id;
        this.state.articleType = this.props.location.type;
        console.log(this.state.articleType);
        this.handleProp();
        console.log(this.props.location.id);
    }


    handleProp(){
        console.log("HandleProp called.");
        switch(this.state.articleType){
            case 'book':{
                this.setState({fieldList: fieldList[0].bookFields});
                break;
            }
            case 'journal':{
                this.setState({fieldList: fieldList[0].journalArticleFields});
                break;
            }
            case 'chapter':{
                this.setState({fieldList: fieldList[0].bookChapterFields});
                break;
            }
            case 'proceeding':{
                this.setState({fieldList: fieldList[0].proceedingFields});
                break;
            }
            case 'research-paper':{
                this.setState({fieldList: fieldList[0].researchPaperFields});
                break;
            }
            case 'article':{
                this.setState({fieldList: fieldList[0].periodicalArticleFields});
                break;
            }
            case 'report':{
                this.setState({fieldList: fieldList[0].reportFields});
                break;
            }
            default:{
                console.log("Invalid Choice for form selection. DO NOT TAMPER.");
                break;
            }
        }
        // Push all the required fields into an array for form validation.
        var temp = [];
        this.state.fieldList.map((key, index)=>{
            if(key.required) {
                temp.push(key.id);
            }
        });
        // Setstate not working in array. Please fix this. :'( 
        this.state.compulsoryFields = temp;
        console.log("Changed the state a/c the prop.");
        console.log(this.state.articleType);
    }

    render(){
        var context = this;
        return(
            <div>
                <TopBar></TopBar>

            <div> 
                <div>
                    <Typography>
                        Enter the information related to {this.props.articleType}.
                    </Typography>
                    <br />
                    <Typography>
                        ( <span style = {{color: 'red'}}>*</span> denotes a required field )
                    </Typography>
                    <Table>
                        <TableBody>
                            { this.state.fieldList.map(function(item, index){
                                return (
                                    <TableRow key = {index}>
                                        <TableCell>
                                            {item.description}
                                            {item.required && <span style = {{color: 'red'}}>*</span> }
                                        </TableCell>
                                        <TableCell>
                                            <TextField 
                                            required = {item.required}
                                            name = "somename"
                                            id = "someid"
                                            className = "someClassName"
                                            key = {index}
                                            onChange = {context.inputChange(item.id)} placeholder = "Begin typing..." />
                                        </TableCell>
                                    </TableRow>
                                );   
                            })}
                            <TableRow>
                                <TableCell></TableCell>
                                <TableCell>
                                    <Button size = "large" color = "primary" onClick = {this.handleSubmit}>
                                        Submit.
                                    </Button>
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </div>
            </div>
            </div>
        )
    }
}
